mapper.select:
	SELECT * FROM (
  		SELECT ROWNUM rnum, tb.* FROM (
    		SELECT auctionNum, subject, userId,content,species,hitCount,amount,aucEnd
    		FROM auction a JOIN
    		member m ON a.num=m.num





    		ORDER BY auctionNum DESC

  		) tb WHERE ROWNUM <= #{end}
	) WHERE rnum >= #{start}

	SELECT tb1. * FROM(
  		SELECT saveFilename
  		FROM auctionPhoto
  		WHERE  auctionNum=#{auctionNum}
  		ORDER BY auctionNum

  	)tb1 WHERE ROWNUM =1

SELECT photoNum, saveFilename, auctionNum
FROM auctionPhoto
WHERE auctionNum=#{auctionNum}

mapper.update:
UPDATE auction SET minPrice=#{minPrice}, buyPrice=#{buyPrice},
aucEnd=#{aucEnd}, amount=#{amount}, species=#{species}, subject=#{subject},
content=#{content}
WHERE auctionNum=#{auctionNum}
