mapper.select:
   		SELECT * FROM (
		    SELECT ROWNUM rnum, tb.* FROM (
			     SELECT bookmarkNum, b.num, b.hostNum, address, cost, subject, userName, profile
			     FROM host h JOIN member m ON h.hostNum=m.num JOIN bookmark b ON b.num=m.num
			     WHERE b.num=#{num}
				 ORDER BY bookmarknum DESC

	     	 ) tb WHERE ROWNUM <= #{end}
	   	 ) WHERE rnum >= #{start}
