mapper.select:
		SELECT * FROM (
		    SELECT ROWNUM rnum, tb.* FROM (
			     SELECT hostNum, address, cost, capacity, subject, content, userName, profile 
			        FROM host h JOIN member m ON h.hostNum=m.num





              ORDER BY hostNum DESC

	        ) tb WHERE ROWNUM <= #{end}
	    ) WHERE rnum >= #{start}

SELECT hostNum, userName, profile, address, capacity, cost, subject, content
FROM host h JOIN member m ON h.hostNum=m.num
WHERE hostNum=#{hostNum}

SELECT * FROM (
    SELECT ROWNUM rnum, tb.* FROM (

		SELECT profile, userName, h.created, content
		FROM hostReview h JOIN reserComplete c ON h.completeNum=c.completeNum JOIN reservation r ON c.reservationNum = r.reservationNum JOIN member m ON r.num=m.num
	WHERE h.hostNum=#{hostNum}
ORDER BY h.hostNum DESC

        ) tb WHERE ROWNUM <= #{end}
    ) WHERE rnum >= #{start}
