mapper.select:
		SELECT*FROM(
   			 SELECT ROWNUM rnum,tb.* FROM(
        		SELECT photoNum, species, saveFilename, subject, content, m.userName,
          			TO_CHAR(p.created, 'YYYY-MM-DD') created, hitCount 
        		 FROM photoGal p JOIN member m ON p.num=m.num





     	    	 ORDER BY photoNum DESC

     		 )tb WHERE ROWNUM<=#{end}
  		)WHERE RNUM >= #{start}

		select*from(
		    select ROWNUM rnum, tb.* from(
   			     select replyNum, p.photoNum,m.num, userName, userId, p.created, p.content
         	    from photoGalReply p JOIN photoGal g ON p.photoNum=g.photoNum
         		    JOIN member m ON g.num=m.num
         	   ORDER BY replyNum DESC

   			 ) tb WHERE ROWNUM<=#{end}
		) WHERE RNUM >= #{start}
