mapper.select:
SELECT NVL(COUNT(reservationNum), 0) FROM reservation r JOIN member m ON r.hostNum=m.num
userName=#{searchHost}
AND (r.num=#{num})

SELECT * FROM (
	SELECT ROWNUM rnum, tb.* FROM (
		SELECT h.hostNum, userName, address, reservationNum, accept, TO_CHAR(checkIn,'YYYY/MM/DD') checkIn, TO_CHAR(checkOut,'YYYY/MM/DD') checkOut 
		FROM reservation r JOIN host h ON h.hostNum=r.hostNum JOIN member m ON m.num=h.hostNum



	     	    userName=#{searchHost}




	     	AND (r.num=#{num})

		ORDER BY reservationNum DESC

	) tb WHERE ROWNUM <= #{end}
) WHERE RNUM >= #{start}

mapper.update:
UPDATE reservation 
SET checkIn=#{checkIn, jdbcType=VARCHAR}, checkOut=#{checkOut, jdbcType=VARCHAR}, pet_su=#{pet_su}, pet_type=#{pet_type}, totalCost=#{totalCost}
WHERE reservationNum=#{reservationNum}
